mapper.delete:
DELETE FROM db_data 
WHERE cust_nm = #{cust_nm}
  AND seq = #{seq}

mapper.select:
SELECT seq ,seq AS rlseq ,cust_nm ,cust_tel ,comment ,gender ,age ,memo ,addr ,order_cnt ,order_amount ,order_date ,expire_date ,manager ,(SELECT user_nm FROM db_data A, user B WHERE A.manager = B.user_id AND A.seq = rlseq) AS manager_nm ,following_date ,site ,reg_date ,mod_date ,CASE WHEN gd_cd = '001' THEN '탑스핏' ELSE '' END AS gd_cd ,CASE WHEN gd_buyType = '01' THEN '접수' WHEN gd_buyType = '02' THEN '부재' WHEN gd_buyType = '03' THEN '보류' WHEN gd_buyType = '04' THEN '구매완료' WHEN gd_buyType = '05' THEN '재구매' ELSE '접수' END AS gd_buyType ,(SELECT ((SELECT sum(inp_cnt) FROM goods_dt WHERE gdcd='001') - (SELECT sum(order_cnt) FROM db_data WHERE gd_cd='001' AND gd_buyType='04')) AS '재고' FROM dual) AS cnt_sum ,nvl(call_succRate,"0") AS call_succRate ,nvl(call_cnt,"0") AS call_cnt FROM db_data
AND manager = #{manager}
AND manager IN (SELECT user_nm FROM user WHERE team_cd = ${team_cd})
AND manager = ''
AND reg_date > STR_TO_DATE(#{srh_sta_date}, '%Y%m%d')
AND reg_date < STR_TO_DATE(#{srh_end_date}, '%Y%m%d')
AND ( cust_nm like '%${srh_key}%' OR cust_tel like '%${srh_key}%' OR comment like '%${srh_key}%' OR manager like '%${srh_key}%' OR site like '%${srh_key}%'
)
ORDER BY seq DESC LIMIT ${page_size} OFFSET ${page_scope}

mapper.update:
UPDATE db_data 
SET
	  cust_tel = #{cust_tel}
	 ,comment = #{comment}
	 ,gender = #{gender}
	 ,age = #{age}
	 ,memo = #{memo}
	 ,addr = #{addr}
	 ,order_cnt = #{order_cnt}
	 ,order_amount = #{order_amount}
	 ,order_date = #{order_date}
	 ,bank_account = #{bank_account}
	 ,card_number = #{card_number}
	 ,expire_date = #{expire_date}
	 ,manager = #{manager}
	 ,following_date = #{following_date}

	 ,mod_date = now()
WHERE seq = #{seq}

UPDATE
team SET
team_nm = #{team_nm},
team_info = #{team_info}
WHERE seq = #{seq}

UPDATE goods_dt 
SET
		inp_cnt = #{inp_cnt}
      , mod_date = now()
      , mod_id = #{mod_id}
      , gd_memo = #{gd_memo}
WHERE   seq = #{seq}

UPDATE user A 
SET	A.user_nm = #{user_nm}
,A.grade = #{grade}
,A.team_cd = #{team_nm}

,A.password = #{password}

WHERE seq = #{seq}
